setTarget(message["chat"]["id"]);
if(message["text"] != "") {
	var weather_url = "https://query.yahooapis.com/v1/public/yql?q=select%20*%20from%20weather.forecast%20where%20woeid%20in%20(select%20woeid%20from%20geo.places(1)%20where%20text%3D%22"+ message["text"] +"%22)%20and%20u%3D%27c%27&format=json&env=store%3A%2F%2Fdatatables.org%2Falltableswithkeys"
	var xhr = new XMLHttpRequest();
	xhr.open("GET", weather_url, true);
	xhr.send();
	xhr.onreadystatechange = function() {
		if(xhr.readyState == 4 && xhr.status == 200) {
			respond = JSON.parse(xhr.responseText);
			weather_item = respond.query.results.channel.item;
			desc = weather_item.title;
			high = weather_item.forecast[0].high;
			low = weather_item.forecast[0].low;
			text = weather_item.forecast[0].text;
			setContent("Weather " + desc + ": High at " + high + "ºC, low at " + low + "ºC, " + text + " now.");
			sendMessage();
		}
	}
}
